import React, { useState, useEffect } from 'react';
import './trendbar.css';
import TrendOption from '../trend-option/trend-option';
import mock_data from '../../mock/mock.json';

function Trendbar() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        console.log('Fetching data...');
        const response = await fetch('http://localhost:5001/get_words', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(mock_data)
        });
        const data = await response.json();
        console.log('Data received:', data);
        setData(data);
      } catch (error: any) {
        console.error('Error:', error);
        setError(error);
      } finally {
        setLoading(false); // Update the loading state in both success and error cases
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="trendbar-loading">
        <div className="spinner"></div>
        <p>Loading...</p>
      </div>
    );
  }

  if (error) {
    return <div>Error: {error}</div>;
  }
  if (data != null) {
    let wordList = data["words"] as string[];
    options = wordList.map((elem, idx) => <TrendOption key={idx} name={elem} />);
    return (
      <div className="trendbar">
        <div className="trendbar-title">Trending </div>
        <div className="trendbar-options">{options}</div>
      </div>
    );
  }
}

export default Trendbar;
